import {useState} from "react";
import "./App.css";

import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faPencilAlt, faTrash} from "@fortawesome/free-solid-svg-icons";

function App() {
  const [patienter, setPatienter] = useState([
    {id: 12345, plats: "101", datum: "23-09-30", fvk: "LEB"},
    {id: 67890, plats: "111", datum: "23-10-10", fvk: "MD"},
  ]);
  //  state för ny patient - start in = input
  const [inPlats, setInPlats] = useState("");
  const [inDatum, setInDatum] = useState("");
  const [inKontakt, setInKontakt] = useState("");

  //  state för redigering
  const [redigeradPatient, setRedigeradPatient] = useState(null);

  //funktioner som ska vara i appen
  function deletePatient(id) {
    setPatienter((nuvPatienter) => {
      return nuvPatienter.filter((p) => p.id !== id);
    });
  }

  function handleSubmit(e) {
    e.preventDefault();
    const nyPatient = {
      id: new Date().getTime(),
      plats: inPlats,
      datum: inDatum,
      fvk: inKontakt,
    };
    setPatienter((currentP) => {
      return [...currentP, nyPatient];
    });
  }

  //funktion för att starta redigering för en pefintlig patient
  function startRedigering(p) {
    setRedigeradPatient(p);
  }
  //funktion för aatt sparaa ändringar i en befintlig patient
  function sparaRedigering() {
    if (redigeradPatient) {
      setPatienter((prevPat) => {
        return prevPat.map((p) => {
          if (p.id === redigeradPatient.id) {
            return redigeradPatient;
          } else {
            return p;
          }
        });
      });
      setRedigeradPatient(null); //nollställ redigeradPatient efter spara
    }
  }

  function changeData(e, fieldName) {
    const editedPatient = {
      id: redigeradPatient.id,
      datum: redigeradPatient.datum,
      plats: fieldName === "plats" ? e.target.value : redigeradPatient.plats,
      fvk:
        fieldName === "fvk"
          ? e.target.value === ""
            ? redigeradPatient.fvk
            : e.target.value
          : redigeradPatient.fvk,
    };
    setRedigeradPatient(editedPatient);
  }

  function uppdateradPatient() {
    const filtreradPatient = [...patienter].filter(
      (u) => u.id !== redigeradPatient.id
    );
    const uppdateradObjekt = [...filtreradPatient, redigeradPatient];
    setPatienter(uppdateradObjekt);
    setRedigeradPatient("");
  }

  return (
    <div className="App">
      <h1>Teest</h1>
      <br />
      {/* //redigera formulär */}
      <div className="card">
        <form onSubmit={handleSubmit}>
          <div className="form-container">
            <div className="va-container">
              <input
                type="text"
                value={redigeradPatient && redigeradPatient.plats}
                onChange={(e) => changeData(e, "plats")}
              />
              <input
                type="text"
                value={redigeradPatient && redigeradPatient.fvk}
                onChange={(e) => changeData(e, "fvk")}
              />
              <input />
            </div>
            <div className="ho-container">
              <input
                type="date"
                value={redigeradPatient && redigeradPatient.datum}
              />
              <input />
              <input />
            </div>
          </div>
          <div className="btn-container">
            <button onClick={uppdateradPatient}>Uppdatera</button>
          </div>
        </form>
      </div>
      <br />
      {/* ny patient form - start */}
      <div className="card">
        <form onSubmit={handleSubmit}>
          <div className="form-container">
            <div className="va-container">
              <input
                type="text"
                value={inPlats}
                onChange={(e) => setInPlats(e.target.value)}
              />
              <input
                type="text"
                value={inKontakt}
                onChange={(e) => setInKontakt(e.target.value)}
              />
              <input />
            </div>
            <div className="ho-container">
              <input
                type="date"
                value={inDatum}
                onChange={(e) => setInDatum(e.target.value)}
              />
              <input />
              <input />
            </div>
          </div>
          <div className="btn-container">
            <button>Spara</button>
          </div>
        </form>
      </div>

      {/* tabell av patienter */}
      <table className="table">
        <thead>
          <tr>
            <th>Plats</th>
            <th>Datum</th>
            <th>FVK</th>
          </tr>
        </thead>
        <tbody>
          {patienter.map((p) => {
            return (
              <tr key={p.id}>
                <td>
                  {p.plats}
                  <span
                    data-tip="Redigera plats"
                    onClick={() => startRedigering("plats")}
                    style={{color: "green", margin: "10px"}}
                  >
                    <FontAwesomeIcon icon={faPencilAlt} size="sm" />
                  </span>
                </td>
                <td>{p.datum}</td>
                <td>{p.fvk}</td>
                {/* <td>
                  <button
                    className="btn-edit"
                    onClick={() =>
                      setRedigeradPatient({
                        id: p.id,
                        plats: p.plats,
                        datum: p.datum,
                        fvk: p.fvk,
                      })
                    }
                  >
                    Redigera
                  </button>
                </td> */}
                <td>
                  <span style={{color: "red", margin: "3px"}}>
                    <FontAwesomeIcon icon={faTrash} size="md" />
                  </span>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default App;
