import React, {useState} from "react";

function App() {
  const [patienter, setPatienter] = useState([
    {id: 12345, plats: "101", datum: "23-09-30", fvk: "LEB"},
    {id: 67890, plats: "111", datum: "23-10-10", fvk: "MD"},
  ]);

  const [redigeradPatient, setRedigeradPatient] = useState(null);

  const startRedigering = (patient) => {
    setRedigeradPatient(patient);
  };

  const sparaRedigering = () => {
    if (redigeradPatient) {
      setPatienter((prevPatienter) => {
        return prevPatienter.map((p) => {
          return p.id === redigeradPatient.id ? redigeradPatient : p;
        });
      });
      setRedigeradPatient(null);
    }
  };

  return (
    <div className="App">
      <h1>Test</h1>
      <table className="table">
        <thead>
          <tr>
            <th>Plats</th>
            <th>Datum</th>
            <th>FVK</th>
            <th>Åtgärder</th>
          </tr>
        </thead>
        <tbody>
          {patienter.map((p) => (
            <tr key={p.id}>
              <td>
                {redigeradPatient === p ? (
                  <input
                    type="text"
                    value={redigeradPatient.plats}
                    onChange={(e) =>
                      setRedigeradPatient({
                        ...redigeradPatient,
                        plats: e.target.value,
                      })
                    }
                  />
                ) : (
                  p.plats
                )}
              </td>
              <td>{p.datum}</td>
              <td>
                {redigeradPatient === p ? (
                  <input
                    type="text"
                    value={redigeradPatient.fvk}
                    onChange={(e) =>
                      setRedigeradPatient({
                        ...redigeradPatient,
                        fvk: e.target.value,
                      })
                    }
                  />
                ) : (
                  p.fvk
                )}
              </td>
              <td>
                {redigeradPatient === p ? (
                  <button onClick={sparaRedigering}>Spara</button>
                ) : (
                  <button onClick={() => startRedigering(p)}>Redigera</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
